    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        // TODO add your handling code here:
        String employeeid = jTextField1.getText();
        String password = jTextField2.getText();
        ConnectDB connectObj = new ConnectDB();
        Connection c1 = connectObj.connecttoDB();
        if(c1 != null) {
            DBQueryExecution obj1 = new DBQueryExecution();
            int val = obj1.addFaculty(c1,employeeid, password);
            if(val == 1) {
                jTextField3.setText(employeeid + " -- Faculty Successfully Registered !! ");
                jButton1.setEnabled(false);
            }
            else
            jTextField3.setText("You have already Registered the Selected Faculty. Select some other faculty.");
        }
    }//GEN-LAST:event_jButton1MouseClicked

